import React from "react";
import { Link } from "react-router-dom";
import Itemcart from './Items/Itemcart'

function Cart({ className, products}) {
  if(products!=null){
    let totalprice = 0
    for(let i = 0;i<products.length;i++){
      totalprice += products[i].price
    }
  }
  return (
    <div>
      <div class="d-flex flex-row flex-wrap justify-content-center mx-5">

      {products!=null ? (
        products.map((data)=>(
          <Itemcart key={data.id} item={data}/>
        ))
      ):(<h2>No items in cart</h2>)}
      
      </div>
      <nav class="navbar fixed-bottom navbar-dark bg-dark">
        <div class="d-flex flex-row col-12 justify-content-end">
          <div class="p-2 my-auto text-white">Total : 12,000</div>
          <div class="p-2 my-auto float-right">
            <Link to="/payment">
              <button type="button" class="btn btn-primary">
                Checkout
              </button>
            </Link>
          </div>
        </div>
      </nav>
    </div>
  );
}
export default Cart
