const CartReducer = (state = [],action) => {
    switch(action.type){
        case 'FETCH_ALL_CART':
            return state
        case 'ADD_CART':
            state.push(action.payload)
            return state
        case 'DELETE_CART':
                const deleteindex = state.findIndex(
                    (item) => item.id === action.payload.id
                );
                state.splice(deleteindex, 1);
            return state
        case 'UPDATE_CART_DECREASE':
                const updateindex = state.findIndex(
                    (item)=> item.id === action.payload.id
                );
                let quantity = action.payload.quantity
                state[updateindex].quantity = quantity-1
            return state
        case 'UPDATE_CART_INCREASE':
            // const updateindex = state.findIndex(
            //     (item)=> item.id === action.payload.id
            // );
            // state[updateindex].quantity = quantity
        return state
        default:
            return state
    }
}

