import React from "react";
import styled from "styled-components";

function Payment() {
  return (
    <div class="d-flex flex-row justify-content-center" id="body">
      <div class="p-2">
        <div class="container" id="paymentBody">
          <div class="d-flex flex-column">
            <div class="p-2">
              <b>Payment Methods</b>
            </div>
            <div class="p-2">
              <div
                alt="paymentmethod_buttons"
                class="d-flex flex-row justify-content-start"
              >
                <div class="p-2">
                  <button type="button" class="btn btn-light">
                    Paypal
                  </button>
                </div>
                <div class="p-2">
                  <button type="button" class="btn btn-light">
                    Credit Card
                  </button>
                </div>
                <div class="p-2">
                  <button type="button" class="btn btn-light">
                    Cash On Delivery
                  </button>
                </div>
              </div>
            </div>
            <div class="p-2">
              <b>Add Payment Details</b>
            </div>
            <div class="p-2">
              <div class="d-flex flex-row justify-content-start">
                <div class="p-2">
                  <div class="d-flex flex-column">
                    <div class="p-1">Card Number</div>
                    <div class="p-1">
                      <input
                        type="text"
                        class="form-control"
                        aria-label="Text input with checkbox"
                      />
                    </div>
                  </div>
                </div>
                <div class="p-2">
                  <div class="d-flex flex-column">
                    <div class="p-1">Expiry Date</div>
                    <div class="p-1">
                      <input
                        type="text"
                        class="form-control"
                        aria-label="Text input with checkbox"
                      />
                    </div>
                  </div>
                </div>
                <div class="p-2">
                  <div class="d-flex flex-column">
                    <div class="p-1">CCV</div>
                    <div class="p-1">
                      <input
                        type="text"
                        class="form-control"
                        aria-label="Text input with checkbox"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div class="p-2">
              <div class="d-flex flex-column">
                <div class="p-1">Promo Code (If any)</div>
                <div class="p-1">
                  <input
                    type="text"
                    class="form-control"
                    style={{ width: 200 + "px" }}
                    aria-label=""
                  />
                </div>
              </div>
            </div>
            <div class="p-2">
              <b>Billing Address</b>
            </div>
            <div class="p-2">
              <div class="d-flex flex-row">
                <div class="p-2">
                  <div class="input-group mb-3">
                    <input class="form-check-input" type="checkbox" value="" />
                  </div>
                </div>
                <div class="p-2">Billing address same as delivery address</div>
              </div>
            </div>
            <div class="p-2">
              <div class="d-flex flex-row justify-content-center">
                <div class="p-2">
                  <button
                    type="button"
                    class="btn btn-light btn-outline-secondary"
                    style={{ width: 100 + "px" }}
                    id="backBtn"
                  >
                    <b>Back</b>
                  </button>
                </div>
                <div class="p-2">
                  <button
                    type="button"
                    class="btn btn-dark"
                    style={{ width: 300 + "px" }}
                    id="confirm"
                  >
                    Your total amount is: 12,000 THB
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div class="p-2">
        <div class="container" id="summaryBody">
          <div class="d-flex justify-content-center">
            <div class="p-2">
              <div class="rectangle">
                <div class="summary">
                  <div class="d-flex flex-column">
                    <div class="p-2">
                      <h3>Summary</h3>
                    </div>
                    <div class="p-2">
                      <div class="d-flex flex-row justify-content-between">
                        <div class="p-2">
                          <h5>Subtotal</h5>
                        </div>
                        <div class="p-2">
                          <h5 id="subtotal">12,000</h5>
                        </div>
                      </div>
                    </div>
                    <div class="p-2">
                      <hr />
                    </div>
                    <div class="p-2">
                      <div class="d-flex flex-row justify-content-between">
                        <div class="p-2">
                          <h5>Total</h5>
                        </div>
                        <div class="p-2">
                          <h5 id="total">12,050</h5>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default styled(Payment)`
  @media only screen and (max-width: 1140px) {
    #navbottom {
      display: none;
    }
    #navtop {
      display: none;
    }
    #bag2 {
      display: block;
    }
  }
  @media only screen and (min-width: 1140px) {
    #navbotton {
      display: block;
    }
    #navtop {
      display: block;
    }
    #bag2 {
      display: none;
    }
