import React, { useState } from 'react';
import PropTypes from 'prop-types';

import { useSelector, useDispatch } from "react-redux";
import { useParams, useHistory } from 'react-router-dom';
import { deletecart, updatecartdecrease, updatecartincrease , quantitydirectupdate} from '../../actions'

function Itemcart({ item }) {

  const dispatch = useDispatch()
  const history = useHistory();

  const onDelete = (id) => {
    dispatch(deletecart({ id: id }));
    history.push('/cart');
  };

  const quantityincrease = (id,quantity) => {
    dispatch(updatecartincrease(id,quantity))
    history.push('/cart');
  }

  const quantitydecrease = (id,quantity) => {
    if(quantity!=0){
      dispatch(updatecartdecrease(id,quantity))
      history.push('/cart');
    }
  }

  const quantitydirect = (id,quantity) => {
    dispatch(quantitydirectupdate(id,quantity))
  }

  return (
    <div className="p-2 col-12 col-md-6 col-lg-4">
          <div className="card">
            <div className="card-header">
              <div className="d-flex flex-row justify-content-end">
                <div className="p-2">
                  <button type="button" className="close" onClick={() => {if(window.confirm('Delete from cart?')){onDelete(item.id)};}} aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
              </div>
            </div>
            <div className="card-body">
              <h5 className="card-title">{item.name}</h5>
              <div className="d-flex flex-row justify-content-around">
                <div className="p-2 col-4">
                  <img
                    src={item.imageURL}
                    className="card-img-top"
                    alt="..."
                  ></img>
                </div>
                <div className="p-2">
                  <div className="d-flex flex-column align-items-center">
                    <div className="p-2">
                      <p className="card-text">{item.description}</p>
                    </div>
                    <div className="p-2">
                      <div class="d-flex flex-row justify-content-center">
                        <div class="p-2">
                          <button class="btn btn-secondary" onClick={()=>{quantitydecrease(item.id,item.quantity)}}>-</button>
                        </div>
                        <div class="p-2">
                          <input onChange={()=>{quantitydirect(item.id,)}} style={{height:38+'px',width:40+'px',textAlign:'center',marginLeft:5+'px',marginRight:5+'px'}} placeholder={item.quantity}/>
                        </div>
                        <div class="p-2">
                          <button class="btn btn-secondary" onClick={()=>{quantityincrease(item.id,item.quantity)}}>+</button>
                        </div>
                      </div>
                    </div>
                    <div className="p-2">
                      <h3>{item.price} THB</h3>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
  );
}

Itemcart.propTypes = {
  item: PropTypes.object.isRequired,
};

export default Itemcart
