import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { useDispatch } from "react-redux";
import { useHistory } from 'react-router-dom';
import { deletefavorite , addcart } from '../../actions'

function Itemfavorite({ item }) {

  const dispatch = useDispatch()
  const history = useHistory();

  const favoritedelete = (id) => {
      dispatch(deletefavorite())
  }

  return (
        <div className="p-2 col-12 col-md-6 col-lg-4">
            <div className="card" style={{width: 20+"rem"}}>
                <div className="card-header">
                    <div className="d-flex flex-row justify-content-end">
                        <div className="p-2">
                            <button type="button" className="close" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                    </div>
                </div>
                <img src={item.imageURL} class="card-img-top" alt="..."/>
                <div className="card-body">
                    <h5 className="card-title">{item.name}</h5>
                    <div className="d-flex flex-column align-items-center">
                        <div className="p-2">
                            <p className="card-text">{item.description}</p>
                        </div>
                        <div className="p-2">
                            <h3>{item.price} THB</h3>
                        </div>
                        <div class="d-flex flex-row justify-content-center">
                            <div class="p-2">
                                <a href="#" class="btn btn-success" onClick={() => {if(window.confirm('Add to cart?')){dispatch(addcart(item));};}}>Add To Cart</a>
                            </div>
                            <div class="p-2">
                                <Link to={`/detail/${item.id}`}>
                                    <a href="#" className="btn btn-danger">
                                    Detail
                                    </a>
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
  );
}

Itemfavorite.propTypes = {
  item: PropTypes.object.isRequired,
};

export default Itemfavorite
