import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import 'react-confirm-alert/src/react-confirm-alert.css';

const addtocart = (item) => {
    var oldItems = JSON.parse(localStorage.getItem("cartitems")) || [];

    var newItem = {
        id: item.id,
        name: item.name,
        imageURL: item.imageURL,
        price: item.price,
        rating: item.rating,
        description : item.description,
        quantity: 1
    }

    let conItem = oldItems.concat(newItem)
    
    localStorage.setItem("cartitems", JSON.stringify(conItem))
}

function Item({ item, className }) {

  return (
    <div class="p-2 col-12 col-md-6 col-lg-4">
              <div class="card" style={{width: 18+'rem'}}>
                <img src={item.imageURL} class="card-img-top" alt="..." />
                <div class="card-body">
                  <b>
                    <h5 class="card-title">{item.name}</h5>
                  </b>
                  <div class="d-flex flex-row justify-content-start">
                  {[...Array(item.rating)].map((elementInArray, i) => ( 
                      <div class="p-2" key={i}> <i class="fa fa-heart" style={{'font-size': 24 + 'px', 'color': 'grey'}}></i> </div> 
                      ) 
                  )}
                  </div>
                  <br />
                  <p class="card-text">{item.description}</p>
                  <div class="d-flex flex-row justify-content-center">
                    <b>
                      <p> {item.price} THB</p>
                    </b>
                  </div>
  
                  <div class="d-flex flex-row justify-content-center">
                    <div class="p-2">
                        <button class="btn btn-success" onClick={() => {if(window.confirm('Add to cart?')){addtocart(item)};}}>
                            Add To Cart
                        </button>
                    </div>
                    <div class="p-2">
                      <Link to={`/detail/${item.id}`}>
                        <a href="#" class="btn btn-danger">
                          Detail
                        </a>
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>
  );
}

Item.propTypes = {
  item: PropTypes.object.isRequired,
  className: PropTypes.string.isRequired
};

export default Item
