//Product
export const fetchallproduct = () => {
    return {
        type: 'FETCH_ALL_PRODUCT'
    }
}

//Cart
export const fetchallcart = () => {
    return {
        type: 'FETCH_ALL_CART'
    }
}
export const addcart = (item) => {
    return {
        type: 'ADD_CART',
        payload: item
    }
}
export const deletecart = (id) => {
    return {
        type: 'DELETE_CART',
        payload: id
    }
}
export const updatecartdecrease = (id,quantity) => {
    return {
        type: 'UPDATE_CART_DECREASE',
        payload: {id:id,quantity:quantity}
    }
}
export const updatecartincrease = (id,quantity) => {
    return {
        type: 'UPDATE_CART_INCREASE',
        payload: {id:id,quantity:quantity}
    }
}
export const quantitydirectupdate = (id,quantity) => {
    return {
        type: 'UPDATE_CART',
        payload: {id:id,quantity:quantity}
    }
}

//Favorite
export const fetchallfavorite = () => {
    return {
        type: 'FETCH_ALL_FAVORITE'
    }
}
export const addfavorite = () => {
    return {
        type: 'ADD_FAVORITE'
    }
}
export const deletefavorite = () => {
    return {
        type: 'DELETE_FAVORITE'
    }
}

//Login
export const loggedin = () => {
    return {
        type: 'loggedIn'
    }
}
