const CustomersReducer = (state = ['FALSE'],action) => {
    switch(action.type){
        case 'REGISTER':
                state.push(action.payload)
            return state
        case 'LOGIN':
                const customerindex = state.findIndex(
                    (user) => user.username.username = action.payload.username
                );
                if(customerindex<0){
                    alert('Username Not Found')
                }else{
                    if(state[customerindex].password=action.payload.password){
                        return state
                    }

                }
        default:
            return state
    }
}

export default CustomersReducer
