import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { useDispatch } from "react-redux";
import { addcart , addfavorite } from '../../actions'
import swal from 'sweetalert';
function Item({ item }) {

  const dispatch = useDispatch()

  const onAddCart = (item) => {
    dispatch(addcart(item))
    swal("Added To Cart!",'', "success");
  }

  const onAddFav = (item) => {
    dispatch(addfavorite(item));
    swal("Added To Favorite!",'', "success");
  }

  return (
    <div className="p-2 col-12 col-md-6 col-lg-4 my-2">
              <div className="card" style={{width: 18+'rem'}}>
                <Link to={`/detail/${item.id}`}>
                  <img src={item.imageURL} style={{width:286+'px',height:286+'px'}} className="card-img-top" alt="..." />
                </Link>
                <div className="card-body" >
                  <div className="d-flex flex-column">
                    <div className="p-2 mx-2">
                      <b>
                        <h5 className="card-title">{item.name}</h5>
                      </b>
                    </div>
                    <div className="p-2">
                      <div className="d-flex flex-row justify-content-start">
                      {[...Array(item.rating)].map((elementInArray, i) => ( 
                          <div className="p-2" key={i}> <i className="fa fa-heart" style={{'fontSize': 24 + 'px', 'color': '#FC3B41'}}></i> </div> 
                          ) 
                      )}
                      </div>
                    </div>
                    <div className="p-2">
                      <div className="d-flex flex-row justify-content-start mx-2">
                        <b>
                          <h5 className="font-weight-normal"> {item.price} THB</h5>
                        </b>
                      </div>
                    </div>
                  </div>
                  
                  {logged ? (
                  <div><li><div class="text-white" style={{marginTop:27+'px'}}><b>Hi!, </b>{username}</div></li></div>
                    ):(<div><li
                      className={`nav-item  ${
                        props.location.pathname === "/login" ? "active" : ""
                      }`}
                    >
                      <Link className="nav-link" to="/login">
                        <b>Login</b>
                      </Link>
                    </li>
                    <li
                      className={`nav-item  ${
                        props.location.pathname === "/register" ? "active" : ""
                      }`}
                    >
                      <Link className="nav-link" to="/register">
                        <b>Register</b>
                      </Link>
                    </li></div>)}

                  <div className="d-flex flex-row justify-content-center">
                    <div className="p-2">
                        <button className="btn" style={{backgroundColor:'#5F7A61',color:'white'}} onClick={() => {onAddCart(item)}}>
                            Add To Cart
                        </button>
                    </div>
                    <div className="p-2">
                        
                          <button className="btn btn-outline-danger mx-2" type="submit" onClick={() => {onAddFav(item)}}>
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="40"
                              height="20"
                              fill="currentColor"
                              className="bi bi-heart"
                              viewBox="0 0 16 16"
                            >
                              <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z" />
                            </svg>
                          </button>
                        
                    </div>
                  </div>
                </div>
              </div>
            </div>
  );
}

Item.propTypes = {
  item: PropTypes.object.isRequired,
};

export default Item
