const cartItem = () => {
    let items = localStorage.getItem("cart_items")
    return items
}

let currentProductId = 9;

const CartReducer = (state = cartItem(),action) => {
    switch(action.type){
        case 'FETCH_ALL_CART':
            return state
        case 'ADD_CART':
            console.log('state',state)
            console.log('action',action)
            break
        case 'DELETE_CART':
            return state
        case 'UPDATE_CART':
            return state
        default:
            return state
    }
}

export default CartReducer
