import React from "react";
import { useParams } from "react-router";
import PropTypes from 'prop-types';
import { useDispatch } from "react-redux";
import { addcart } from '../actions'
import swal from 'sweetalert';

function Detail({ products }) {

  const { id } = useParams()
  const dispatch = useDispatch()

  const onAddCart = (item) => {
    dispatch(addcart(item))
    swal("Added To Cart!",'', "success");
  }

  return (
      <div style={{backgroundColor:'#2E2E2E',padding:45+'px',border:'1px ' + 'hidden ' + 'black',borderRadius:10+'px'}}>
        <div className="d-flex flex-row justify-content-center">
          <div className="p-2">
            <img alt="..." src={products[id].imageURL} style={{height:400+'px',width:400+'px'}}className="rounded"/>

          </div>
          <div className="p-2">
            <div className="d-flex flex-column">
              <div className="p-2">
                <p>
                  <h2 className="text-white">{products[id].name}</h2>
                </p>
              </div>
              <div className="p-2">
                <h4 className="text-white">
                  <b>{products[id].price} THB</b>
                </h4>
              </div>
              <div className="p-2">
                <p className="text-white">{products[id].description}</p>
              </div>
              <div className="p-2">
                    <p class="font-weight-light text-white">Stock : {products[id].quantity}</p>
              </div>
              <div className="p-2">
                <button className="btn" style={{backgroundColor:'#608E62',color:'white'}} onClick={() => {onAddCart(products[id])}}>
                  Add To Cart
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

  );
}

Detail.propTypes = {
  products: PropTypes.array.isRequired
};

export default Detail
