import data from '../app/data'

const ProductsReducer = (state = data,action) => {
    switch(action.type){
        case 'FETCH_ALL_PRODUCT':
            return state
        case 'ADD_PRODUCT':
            let new_product = {
                name:action.payload.name,
                description
            }
            return state
        case 'UPDATE_PRODUCT':
            return state
        case 'DELETE_PRODUCT':
            return state
        default:
            return state
    }
}

export default ProductsReducer
