const CartReducer = (state = [],action) => {
    switch(action.type){
        case 'FETCH_ALL_CART':
            return state
        case 'ADD_CART':
            state.push(action.payload)
            return state
        case 'DELETE_CART':
            // let id = action.payload
            // state.splice(id,1)
            const productIndex = state.findIndex(
                (item) => item.id === action.payload
              );
              state.splice(productIndex, 1);
            return state
        case 'UPDATE_CART':
            return state
        default:
            return state
    }
}

export default CartReducer
