import React from "react";
import styled from 'styled-components';

function Login() {
    return (
        <div>
            <div class="container">
                <div class="blurred-box">
                    <div class="user-login-box">
                            <span class="user-icon"></span>
                            <div class="user-name">Plant</div>
                            <input type="text" class="user-username" placeholder="Username"/>
                            <input type="text" class="user-password" placeholder="Password"/>
                            <button type="button" class="btn btn-dark">Login</button>
                            <button type="button" class="btn btn-success">Signin</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
export default styled(Login)`
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }
    container {
        width: 100vw;
        height: 100vh;
    }
    body {
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-size: cover;
        background-position: top;
        background-image: url(https://images.unsplash.com/photo-1565701175719-5ef844931183?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1648&q=80);
        width: 100%;
        height: 100%;
        font-family: Arial, Helvetica, sans-serif;
        letter-spacing: 0.02em;
        font-weight: 400;
    }
    .blurred-box {
        position: relative;
        width: 250px;
        height: 350px;
        top: calc(50% - 175px);
        left: calc(50% - 125px);
        background: inherit;
        border-radius: 2px;
        overflow: hidden;
    }
    .blurred-box:after {
        content: '';
        width: 300px;
        height: 300px;
        background: inherit;
        position: absolute;
        left: -20px;
        right: 0;
        top: -25px;
        bottom: 0;
        box-shadow: inset 0 0 0 200px rgba(255, 255, 255, 0.05);
        filter: blur(10px);
    }
    .user-login-box {
        position: relative;
        margin-top: 30px;
        text-align: center;
        z-index: 1;
    }
    .user-login-box > * {
        display: inline-block;
        width: 200px;
    }
    .user-icon {
        width: 100px;
        height: 100px;
        position: relative;
        border-radius: 50%;
        background-size: contain;
        background-image: url(https://i.pinimg.com/564x/04/71/1a/04711aaf1a2247dcf34e52ce57630ead.jpg);
    }
    .user-name {
        margin-top: 15px;
        margin-bottom: 15px;
        color: #fff;
    }
    input.user-username {
        margin-bottom: 4px !important;
    }
    input.user-username, input.user-password {
        display: block;
        width: 200px;
        height: 18px;
        opacity: 0.4;
        border-radius: 2px;
        padding: 15px 15px;
        border: none;
        margin: 0 auto;
        outline: none;
    }
`;
