import React from "react";
import { Link } from "react-router-dom";
import Itemcart from './Items/Itemcart'

function Cart({ products }) {
  let totalprice = 0
  if(products!=null){
    for(let i = 0;i<products.length;i++){
      totalprice += products[i].price*products[i].quantity
    }
  }else{
    totalprice = 0
  }
  return (
    <div>
      <div class="container" style={{backgroundColor:"#f3f3f3",borderRadius:10+'px',border:1+'px '+'hidden '+ 'black',padding:45+'px'}}>

      <div className="d-flex flex-row flex-wrap justify-content-center mx-5">

      {products.length>0 ? (
        products.map((data)=>(
          <Itemcart key={data.id} item={data}/>
        ))
      ):(<h3 className="font-weight-normal">No items in cart</h3>)}
      </div>
      </div>
      <nav className="navbar fixed-bottom navbar-dark bg-dark">
        <div className="d-flex flex-row col-12 justify-content-end">
          <div className="p-2 my-auto text-white">Total : {totalprice}</div>
          <div className="p-2 my-auto float-right">
            <Link to={`/payment`}>
              <button type="button" className="btn" style={{backgroundColor:'#608E62',color:'white'}}>
                Checkout
              </button>
            </Link>
          </div>
        </div>
      </nav>
    </div>
  );
}
export default Cart
