const cartItem = () => {
    let items = localStorage.getItem("cart_items")
    if(items!=null){
        return items
    }else{
        return []
    }
}

let currentProductId = 9;

const CartReducer = (state = [],action) => {
    switch(action.type){
        case 'FETCH_ALL_CART':
            return state
        case 'ADD_CART':
            
            state.push(currentProductId,action.payload)
            return state
        case 'DELETE_CART':
            return state
        case 'UPDATE_CART':
            return state
        default:
            return state
    }
}

export default CartReducer
