const CustomersReducer = (state = [],action) => {
    switch(action.type){
        case 'REGISTER':
                state.push(action.payload)
            return state
        case 'LOGIN':
                const deleteindex = state.findIndex(
                    (item) => item.id === action.payload.id
                );
                state.splice(deleteindex, 1);
            return state
        default:
            return state
    }
}

export default CustomersReducer
