import React from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import styled from "styled-components";
import { Link } from "react-router-dom";
import slide1 from "../assets/images/09.jpg";
import slide2 from "../assets/images/02.png";
import slide3 from "../assets/images/07.jpg";
let data = require('../app/data.json');

function rating(rating){
  let result
  for(let i = 1;i<rating;i++){
    result += `<i class="fa fa-heart"></i>`
  }
}

function Home() {
  return (
    <div>
      <div class="container my-4">
        <Carousel>
          <div>
            <img src={slide1} alt="..." />
            <p className="legend">Cactus 1</p>
          </div>
          <div>
            <img src={slide2} alt="..." />
            <p className="legend">Cactus 2</p>
          </div>
          <div>
            <img src={slide3} alt="..." />
            <p className="legend">Cactus 3</p>
          </div>
        </Carousel>
      </div>

      <div class="container">
        <h2 class="header-top header-bottom">All Products</h2>
        <div class="d-flex flex-row flex-wrap justify-content-center width-100 mx-5">
          {data.map((data)=>(
              <div class="p-2 col-4">
              <div class="card">
                <img src={data.imageURL} class="card-img-top" alt="..." />
                <div class="card-body">
                  <b>
                    <h5 class="card-title">{data.name}</h5>
                  </b>
                  {rating(data.rating)}
                  <br />
                  <p class="card-text">{data.description}</p>
                  <div class="d-flex flex-row justify-content-center">
                    <b>
                      <p> {data.price} THB</p>
                    </b>
                  </div>
  
                  <div class="d-flex flex-row justify-content-center">
                    <div class="p-2">
                      <Link to="">
                        <div class="btn btn-success">
                          Add
                        </div>
                      </Link>
                    </div>
                    <div class="p-2">
                      <a href="#" class="btn btn-danger">
                        Detail
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
          </div>
        </div>
      </div>
  );
}

export default styled(Home)`
  body {
    background-color: #ffcccc;
  }
  .fa-heart {
    font-size: 24px;
    color: grey;
  }
`;
