const CustomersReducer = (state = [],action) => {
    switch(action.type){
        case 'REGISTER':
                state.push(action.payload)
            return state
        case 'LOGIN':
                const customerindex = state.findIndex(
                    (user) => user.username === action.payload.username
                );
                if(state[customerindex]!=null&&state[customerindex]!=undefined){
                    if(action.payload.password=state[customerindex].password){
                        state[0]='TRUE'
                        return state
                    }else{
                        alert('Incorrect Password')
                        return state
                    }
                }else{
                    alert('Username Not Found.')
                    return state
                }
                
        default:
            return state
    }
}

export default CustomersReducer
