import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import 'react-confirm-alert/src/react-confirm-alert.css';
import { useSelector, useDispatch } from "react-redux";
import { addcart } from '../../actions'

function Item({ item }) {

  const dispatch = useDispatch()

  return (
    <div className="p-2 col-12 col-md-6 col-lg-4">
              <div className="card" style={{width: 18+'rem'}}>
                <img src={item.imageURL} className="card-img-top" alt="..." />
                <div className="card-body">
                  <b>
                    <h5 className="card-title">{item.name}</h5>
                  </b>
                  <div className="d-flex flex-row justify-content-start">
                  {[...Array(item.rating)].map((elementInArray, i) => ( 
                      <div className="p-2" key={i}> <i className="fa fa-heart" style={{'fontSize': 24 + 'px', 'color': 'grey'}}></i> </div> 
                      ) 
                  )}
                  </div>
                  <br />
                  <div className="d-flex flex-row justify-content-center">
                    <b>
                      <h5> {item.price} THB</h5>
                    </b>
                  </div>
  
                  <div className="d-flex flex-row justify-content-center">
                    <div className="p-2">
                        <button className="btn btn-success" onClick={() => {if(window.confirm('Add to cart?')){dispatch(addcart(item));alert('Success')};}}>
                            Add To Cart
                        </button>
                    </div>
                    <div className="p-2">
                      <Link to={`/detail/${item.id}`}>
                        <a href="#" className="btn btn-danger">
                          Detail
                        </a>
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>
  );
}

Item.propTypes = {
  item: PropTypes.object.isRequired,
};

export default Item
